sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (newValue === '') {
      return;
   }
     var whatneeded = g_form.getValue("u_what_needed");
	 g_form.clearOptions("u_what_needed");

	if(newValue=='hr')
		{			
			g_form.addOption('u_what_needed', 'hr1', 'Human Resources 1');
			g_form.addOption('u_what_needed', 'hr2', 'Human Resources 2');
			g_form.addOption('u_what_needed', 'Other', 'Other values');
			g_form.addOption('u_what_needed', 'Other', 'Hallmark HR');
		}

   if(newValue=='fc')
	   {
		   g_form.addOption('u_what_needed', 'fc1', 'Cards');
		   g_form.addOption('u_what_needed', 'fc2', 'Chocolates');
		   g_form.addOption('u_what_needed', 'fc3', 'Cakes');
		   g_form.addOption('u_what_needed', 'fc4', 'Toys');
		   g_form.addOption('u_what_needed', 'fc5', 'Gifts');
	   }

	if(newValue=='lg')
		{
			g_form.addOption('u_what_needed', 'lg1', 'Legal1');
			g_form.addOption('u_what_needed', 'lg2', 'Legal2');
			g_form.addOption('u_what_needed', 'lg3', 'Legal3');			
		}
	if(isLoading && !g_form.isNewRecord())
		{
			g_form.setValue('u_what_needed',whatneeded);
		}
}
